// Math.min() returns smallest number

// SYNTAX:
// Math.min()
// Math.min(value1)
// Math.min(value1, value2)
// Math.min(value1, value2, /*...*/ valueN)

// PARAMETERS:
// value - zero or more numbers to pick the min from

// RETURNS:
// smallest number

// EXAMPLE:
console.log(Math.min(36, 14, 27)); // 14

// EXAMPLE: with array
const array1 = [20, 30, 10];
console.log(Math.min(...array1)); // 10

// REFERENCE:
// MDN:
// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Math/min
